    public function get($key, $default = null, $deep = false)
    {
        if ($this !== $result = $this->query->get($key, $this, $deep)) {
            return $result;
        }
        if ($this !== $result = $this->attributes->get($key, $this, $deep)) {
            return $result;
        }
        if ($this !== $result = $this->request->get($key, $this, $deep)) {
            return $result;
        }
        return $default;
    }
